import { useState } from 'react'

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const setToGood = () => {
    const newValue = good + 1
    setGood(newValue)
  }

  const setToNeutral = () => {
    const newValue = neutral + 1
    setNeutral(newValue)
  }

  const setToBad = () => {
    const newValue = bad + 1
    setBad(newValue)
  }

  const sum = (p1, p2, p3) => {
    return p1 + p2 + p3
  }

  const clicks = sum(good, neutral, bad)
  const average = (good + bad*(-1))/clicks

  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={setToGood}>good</button>
      <button onClick={setToNeutral}>neutral</button>
      <button onClick={setToBad}>bad</button>
      <h1>statistics</h1>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
      <p>all {clicks}</p>
      <p>average {average} </p>
      <p>positive {} %</p>
    </div>
  )
}

export default App
