import { useState } from 'react'

const Statistics = (props) => {

  const clicks = props.pos + props.neu + props.neg
  const average = (props.pos + props.neg*(-1))/clicks
  const positive = (props.pos/clicks)*100

  if (clicks<1) {
    return(
    <div>
      <p>No feedback given</p>
    </div>)
  } else {
    return(
      <div>
        <h1>statistics</h1>
        <p>good {props.pos}</p>
        <p>neutral {props.neu}</p>
        <p>bad {props.neg}</p>
        <p>all {clicks}</p>
        <p>average {average}</p>
        <p>positive {positive} %</p>
      </div>)
    //  block of code to be executed if the condition is false
  }

}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const setToGood = () => {
    const newValue = good + 1
    setGood(newValue)
  }

  const setToNeutral = () => {
    const newValue = neutral + 1
    setNeutral(newValue)
  }

  const setToBad = () => {
    const newValue = bad + 1
    setBad(newValue)
  }


  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={setToGood}>good</button>
      <button onClick={setToNeutral}>neutral</button>
      <button onClick={setToBad}>bad</button>
      <Statistics pos={good} neu={neutral} neg={bad}/>
    </div>
  )
}

export default App
