import { useState } from 'react'
import Filter from './Filter.js'
import PersonForm from './PersonForm.js'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')
  const arrayLength = persons.length

  function getNames() {
    const names = []
    let i = 0
    do {
        names.push(persons[i].name)
        i++
    } while (i < persons.length)
    return names

  }

  function filterItems(query) {
    const filteredPersons = []
    const filtered = getNames().filter((el) => el.toLowerCase().includes(query.toLowerCase()))
    for (var i = 0; i < arrayLength; i++) {
        if (filtered.includes(persons[i].name)) {
          const person = {name: persons[i].name, number: persons[i].number}
          filteredPersons.push(person)
        }
    }
    return filteredPersons
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNewFilter = (event) => {
    setNewFilter(event.target.value)
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    const person = {name: newName, number: newNumber}
    const copy = [...persons]
    // console.log(copy)
    const names = getNames()

    if (names.includes(person.name)) {
      alert(`${person.name} is already in the phonebook`)
    }

    else {    copy.push(person)
      setPersons(copy)

      setNewName("")
      setNewNumber("")
    }
  }

  console.log(getNames())
  console.log(filterItems("b"))

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filter={newFilter} change={handleNewFilter}/>
      <h2>Add a new number</h2>
      <PersonForm  name={newName} changeName={handleNewName} number={newNumber} changeNum={handleNewNumber} submit={handleSubmit}/>
      <h2>Numbers</h2>
      {filterItems(newFilter).map((person) => (
  <p>{person.name} {person.number}</p>
  ))}
    </div>
  )

}

export default App
