import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState(
    [
    { name: 'Arto Hellas' }
  ]) 

  const [newName, setNewName] = useState('')


  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    const person = {name: newName}
    const copy = [...persons]
    // console.log(copy)
    const names = []
    let i = 0;

    do {
        names.push(copy[i].name);
        i++;
    } while (i < copy.length);

    const caca = person.name
    console.log(caca)

    if (names.includes(caca)) {
      alert(JSON.stringify(caca) + " is already in phonebook")
    }

    else {    copy.push(person)
      setPersons(copy)
      // console.log(persons)}
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={handleSubmit}>
        <div>
          name: <input 
          value={newName}
          onChange={handleNewName} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map((person) => (
      <p>{person.name}</p>
    ))}
    </div>
  )

}

export default App
